mapper.select:
SELECT t.recharge_no AS rechargeNo, #代付单号 t.recharge_money AS rechargeMoney, #代付金额 t.recharge_rate_money AS rechargeRateMoney, #代付手续费 t.bank_code AS bankCode, #收款银行编码 (SELECT sb.bank_name FROM sys_bank sb WHERE sb.bank_code = t.bank_code) AS bankName, #收款银行 t.bank_account AS bankAccount, #收款人账户 t.bank_no AS bankNo, #银行卡号 t.bank_subbranch AS bankSubbranch, #支行名称 t.bank_bin AS bankBin, #联行号 t.province AS province, #所在省 t.city AS city, #所在城市 t.recharge_status AS rechargeStatus,#代付状态 t.third_channel_id AS thirdChannelId,#通道ID t.third_channel_order_no AS thirdChanelOrderNo, #通道交易流水号 t.remarks AS remarks, #驳回就是驳回的原因 t.create_date AS createDate, #创建时间 t.complete_time AS completeTime #代付完成时间 FROM shop_recharge t WHERE t.del_flag= 0
AND t.user_id = #{sysUserId}
AND t.recharge_no = #{rechargeNo}
AND t.third_channel_id = #{thirdChannelId}
AND t.recharge_status = #{rechargeStatus}
AND t.create_date BETWEEN #{beginTime} AND #{endTime}
ORDER BY t.create_date DESC LIMIT ${pageStart},${pageSize}

SELECT COUNT(1) FROM shop_recharge t
WHERE t.del_flag= 0

    AND t.user_id = #{sysUserId}


    AND t.recharge_no = #{rechargeNo}


    AND t.third_channel_id = #{thirdChannelId}


    AND t.recharge_status = #{rechargeStatus}


    AND t.create_date BETWEEN #{beginTime} AND #{endTime}
